<?phpfunction show_the_breadcrumbs() {    global $post;    echo '<div class="breadcrumbs">';    echo '<div class="container">';    echo '<div class="row">';    echo '<div class="col-md-12">';    if ( !is_home() ) {        echo '<a href="'.get_home_url().'">home</a>';        if( is_tag() ) {            echo '<span>'.get_query_var('tag').'</span>';        } elseif( is_404() ) {            echo '<span>not found</span>';        } elseif( is_search() ) {            echo '<span>Search results</span>';        } elseif( is_category() ) {            $cat = get_category(get_current_cat_ID());            if( $cat->parent!=0 ) {                $parent = get_category($cat->parent);                if( $parent->parent != 0 ) {                    $grandparent = get_category($parent->parent);                    echo '<a href="'.get_category_link($grandparent->term_id).'">'.$grandparent->name.'</a>';                    echo '<a href="'.get_category_link($parent->term_id).'">'.$parent->name.'</a>';                    echo '<span>'.$cat->name.'</span>';                } else {                    echo '<a href="'.get_category_link($parent->term_id).'">'.$parent->name.'</a>';                    echo '<span>'.$cat->name.'</span>';                }            } else {                echo '<span>'.$cat->name.'</span>';            }        } elseif( is_single() ) {            $category = get_the_category();            $cat = get_category($category[0]->term_id);            if( $cat->parent != 0 ) {                $parent = get_category($cat->parent);                if( $parent->parent != 0 ) {                    $grandparent = get_category($parent->parent);                    echo '<a href="'.get_category_link($grandparent->term_id).'" >'.$grandparent->name.'</a>';                    echo '<a href="'.get_category_link($parent->term_id).'" >'.$parent->name.'</a>';                    echo '<a href="'.get_category_link($cat->term_id).'" > '.$cat->name.' </a>';                } else {                    echo '<a href="'.get_category_link($parent->term_id).'" > '.$parent->name.'</a>';                    echo '<a href="'.get_category_link($cat->term_id).'" > '.$cat->name.'</a>';                }            } else {                echo '<a href="'.get_category_link($cat->cat_ID).'" >'.$cat->name.'</a>';            }        } elseif ( is_page() ) {            if( $post->post_parent ) {                $anc = get_post_ancestors( $post->ID );                $title = get_the_title();                foreach ( $anc as $ancestor ) {                    echo '<a href="'.get_permalink($ancestor).'">'.get_the_title($ancestor).'</a>';                }                echo '<span>'.$title.'</span>';            } else {                echo '<span>'.get_the_title().'</span>';            }        }    }    elseif ( is_tag() ) {        echo '<span>'.single_tag_title("", false).'</span>';    } elseif ( is_author() ) {        echo '<span>Author Archive</span>';    } elseif ( is_search() ) {        echo '<span>Search Results</span>';    }    echo '</div>';    echo '</div>';    echo '</div>';    echo '</div>';}