<?phpadd_action('wp_enqueue_scripts', 'enqueue_theme_scriptsnstyles');function enqueue_theme_scriptsnstyles() {    /*********** Styles ***********/    wp_register_style('theme-style', THEME_URL . '/style.css');    wp_enqueue_style('theme-style');    /*********** Scripts ***********/    wp_deregister_script('jquery');    wp_register_script('jquery', 'http://ajax.googleapis.com/ajax/libs/jquery/1.12.3/jquery.min.js', array(), '', TRUE);    wp_enqueue_script('jquery');    wp_register_script('theme-main', THEME_URL . '/js/main.js', array('jquery'), '', TRUE);    wp_enqueue_script('theme-main');    wp_register_script('loadmore', THEME_URL . '/js/loadmore.js', array('jquery'), '', TRUE);    wp_enqueue_script('loadmore');    if (is_single()) {        wp_register_script('single', THEME_URL . '/js/single.js', array('jquery'), '', TRUE);        wp_enqueue_script('single');    }}