<?php// get the first category from post_idfunction get_first_category_by_post_id($post_id, $variable_needed = null) {    $all_categories = get_the_category($post_id);    if(count($all_categories) > 0) {        $category = $all_categories[0];        if($variable_needed == 'slug') {            return $category->slug;        } elseif($variable_needed == 'id') {            return $category->term_id;        } elseif($variable_needed == 'link') {            return get_category_link($category);        } else {            return $category->name;        }    }}if ( ! function_exists( 'get_current_cat_ID' ) ) {    function get_current_cat_ID()    {        global $wp_query;        $cat_ID = 0;        if(is_category() || is_single()) {            $cat_ID = get_query_var('cat');        }        return $cat_ID;    }}