<div class="wrap"><h1>Theme Settings</h1><form method="post" action="options.php">    <?php    // This prints out all hidden setting fields    settings_fields( 'ts_option_group' );    do_settings_sections( 'theme-setting-page' );    submit_button();    ?></form></div><script type="text/javascript">    jQuery(document).ready(function($){        $('.upload-btn').click(function(e) {            var clicked = $(this);            e.preventDefault();            var image = wp.media({                title: 'Upload Image',                multiple: false            }).open()                .on('select', function(e){                    var uploaded_image = image.state().get('selection').first();                    console.log(uploaded_image);                    var image_url = uploaded_image.toJSON().url;                    clicked.parent().children('.regular-text').val(image_url);                    console.log(clicked.parent().children('.regular-text'));                });        });    });</script>